mapper.update:
update emp_tb 
set e_rpwd=#{e_rpwd}
where e_email=#{e_email}

mapper.select:
select e_rpwd 
from emp_tb 
where e_email=#{e_email}

mapper.update:
update man_tb 
set m_rpwd=#{m_rpwd}
where m_email=#{m_email}

mapper.select:
select m_rpwd 
from man_tb 
where m_email=#{m_email}

select e_email,e_pwd
from emp_tb
where e_email=#{e_email}
and e_pwd=#{e_pwd}

select emp_ix
from emp_tb
where e_email=#{e_email}

select e_name
from emp_tb
where e_email=#{e_email}

select m_email,m_pwd
from man_tb
where m_email=#{m_email}
and m_pwd=#{m_pwd}

select man_ix
from man_tb
where m_email=#{m_email}

select m_name
from man_tb
where m_email=#{m_email}

mapper.update:
update man_tb
set m_pwd=#{m_pwd},m_tel=#{m_tel}
where man_ix=#{man_ix}

update emp_commute_tb 
    set leavetime = to_char(sysdate, 'HH24:MI')
    where emp_ix = #{emp_ix} and emp_commute_ix = #{emp_commute_ix}

update emp_commute_tb
	set worktime = to_char(sysdate, 'HH24:MI')
	where emp_commute_ix = #{emp_commute_ix}

update emp_commute_tb 
    set daytime = #{daytime}
    where emp_ix = #{emp_ix} and emp_commute_ix = #{emp_commute_ix}
