mapper.select:
 		select count(emp_ix) as total
		from emp_change_tb
		where emp_ix=#{emp_ix}

mapper.update:
update emp_commute_tb
set state=#{state}
where emp_commute_ix=#{emp_commute_ix}

update emp_change_tb
set c_return=#{c_return}
where emp_change_ix=#{emp_change_ix}

 		update emp_change_tb
		set progress=#{progress}
		where emp_change_ix=#{emp_change_ix}
